import React, { Component } from 'react';
import {Grid, Menu, Button, Label} from 'semantic-ui-react';
import './style.scss';
import * as nephews from '../../services/nephewsService';

class Dashboard extends Component {
  state = {
    nephews: []
  };

  componentDidMount = () => {
    nephews.getAll().then(res => {
      this.setState({nephews: res.data.nephews});
    })
  };

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })

  render() {
    return (
      <React.Fragment>
        <Grid style={{minHeight: '100vh'}}>
          <Grid.Column width={4} >
            <Menu vertical style={{width: '100%'}}>
              {
                this.state.nephews.map(neph => (
                  <Menu.Item name='updates' active={this.activeItem === 'updates'} onClick={this.handleItemClick}>
                    <Label>1</Label>
                    {neph.name}
                  </Menu.Item>
                ))
              }

              <Menu.Item name='updates' active={this.activeItem === 'updates'} onClick={this.handleItemClick}>
                <Label>1</Label>
                Updates
              </Menu.Item>
            </Menu>

          </Grid.Column>
          <Grid.Column>
              aa
          </Grid.Column>
        </Grid>
      </React.Fragment>
    )
  }
}

export default Dashboard;
